svg.script:

function toRadians(degrees){
    var pi = Math.PI;
    var radians = (degrees * (pi/180));
    return radians;
}


function circle(radius, angle0, angle1, steps, interval, obj, centerX, centerY){
    var dangle = angle1 - angle0;
    var sangle = dangle  / steps;
    var cx = centerX;
    var cy = centerY;
    //var cx = x - radius * Math.cos(toRadians(angle0));
    //var cy = y + radius * Math.sin(toRadians(angle0));
    angle0r = toRadians(angle0);
    for(var i = 0; i < steps; i++){
        angle0 += sangle;
        x = cx + radius * Math.cos(angle0r);
        y = cy - radius * Math.sin(angle0r);
        $(obj).animate({'svg-cx': x, 'svg-cy': y}, interval / steps);
    }
    return [x, y];
}

function byClassName(className){
    filters = className.split(".");

    if(filters[0] == ""){
        elements = $("svg *");
    }else{
        elements = $(filters[0]);
    }

    var retValue = new Array();
